mapper.update:
update deposit_match_currency_order_user
set status
='CONFIRM',confirm_user=#{adminUser} where id=#{id}

update deposit_match_currency_order_user
set status
='INVALID',confirm_user=#{adminUser} where id=#{id}

mapper.select:
select

from deposit_match_currency_order_user



			status =#{status}


			status <> 'INVALID'



		and order_name =#{name}



		and order_account_no =#{accountNo}
