package com.gop.notify.service.impl;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import com.gop.domain.BrokerNotify;
import com.gop.mapper.BrokerNotifyMapper;
import com.gop.notify.service.BrokerNotifyService;

@Service
public class BrokerNotifyServiceImpl implements BrokerNotifyService {
	@Autowired
	private BrokerNotifyMapper brokerNotifyMapper;

	@Override
	public BrokerNotify getBrokerNotify(int borkerId) {
		// TODO Auto-generated method stub
		return brokerNotifyMapper.selectByBrokerId(borkerId);
	}

}
